import { Dispatch, SetStateAction } from "react";


const fetchProductByAllInputs = async (
  searchTitle: string,
  categoryId: number,
  minPrice: number,
  maxPrice: number | null,
  setProducts: Dispatch<SetStateAction<any[]>>,
  setHttpError: Dispatch<SetStateAction<string>>,
  setPriceError: Dispatch<SetStateAction<boolean>>,
  setLoading: Dispatch<SetStateAction<boolean>>
) => {
  let url = buildSearchUrl(searchTitle, categoryId, minPrice, maxPrice);

  try {
    setLoading(true);
    const responseData = await fetch(url);
    if (minPrice >= 0 && maxPrice > minPrice) {
      setPriceError(false);
      const responseData = await fetch(url);
      if (!responseData.ok) {
        throw new Error("Something went wrong!");
      }
      const responseJson = await responseData.json();
      setProducts(responseJson._embedded.products);
    } else {
      setPriceError(true);
    }
    setLoading(false);
    setPriceError(false); // Update the priceError state
  } catch (error) {
    setLoading(false);
    setHttpError(error.message);
    setPriceError(true); // Update the priceError state
  }
};


export { fetchProductByAllInputs };
