import React, { useState } from "react";
import Registration from "./Components/Registration";
import Login from "./Components/Login";
const AuthPage: React.FC<{ register: boolean }> = (props) => {

  const [registerVisibility, setRegisterVisibility] = useState(props.register);


  const togglePasswordVisibility = () => {
    const passwordInput: any = document.getElementById("user-password");
    const eyeVisibleIcon: any = document.getElementById("eye-visible");
    const eyeUnvisibleIcon: any = document.getElementById("eye-unvisible");
  
    if (passwordInput.type === "password") {
      passwordInput.type = "text";
      eyeVisibleIcon.style.display = "none";
      eyeUnvisibleIcon.style.display = "block";
    } else {
      passwordInput.type = "password";
      eyeVisibleIcon.style.display = "block";
      eyeUnvisibleIcon.style.display = "none";
    }
  };

  console.log(props.register);
  
  return (
    <div className="register-area ptb-100">
      <div className="container-fluid">
        <div className="row">
          <div className="col-lg-7 col-md-6 col-12 p-0">
            <div className="back-pattern">
              <h1 className="pattern-welcome">
                Welcome to our application <br />
                Register & Login
              </h1>
            </div>
          </div>
          <div className="col-md-6 col-12 col-lg-5 col-xl-5 p-0">
            <div className="login">
              <div className="login-form-container">
                {registerVisibility ? (
                  <Registration setVisibility ={setRegisterVisibility} togglePassword={togglePasswordVisibility} />
                ) : (
                  <Login />
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AuthPage;
