import React, { useEffect, useState } from "react";
import { Button, Input, Label, Select } from "semantic-ui-react";
import CategoryModel from "../../../Models/CategoryModel";
import ColorModel from "../../../Models/ColorModel";

const CreateProduct = () => {
  const [categories, setCategories] = useState<CategoryModel[]>([]);
  const [colors, setColors] = useState<ColorModel[]>([]);

  const [images, setImages] = useState<string[]>([]);
  const [imageUrl, setImageUrl] = useState("");

  const [errorMessage, setErrorMessage] = useState("");

  useEffect(() => {
    const fetchCategories = async () => {
      const url = "http://localhost:8080/api/categories";
      const responseData = await fetch(url);
      if (!responseData.ok) {
        throw new Error("Something went wrong during fetching product!");
      }
      const responseJson = await responseData.json();
      setCategories(responseJson._embedded.categories);
    };
    fetchCategories().catch((err) => setErrorMessage(err.message));
  }, []);

  useEffect(() => {
    const fetchProductColors = async () => {
      const url = `http://localhost:8080/api/colors`;
      const responseData = await fetch(url);
      if (!responseData.ok) {
        throw new Error("Something went wrong while fetching colors!");
      }
      const responseJson = await responseData.json();
      setColors(responseJson._embedded.colors);
    };
    fetchProductColors().catch((err: any) => setErrorMessage(err.message));
  }, []);

  const addImageToList = () => {
    setImages((prevImages) => [...prevImages, imageUrl]);
    setImageUrl("")
  };
  if (errorMessage) {
    return <p>{errorMessage}</p>;
  }

  return (
    <div className="content-page">
      <div className="create-product-header text-center">
        <h2>Create Product</h2>
        <p>Errors</p>
      </div>
      <div className="create-product-form">
        <form action="" onSubmit={(e: any) => e.preventDefault()}>
          <Label className="mt-3 mb-1" content="Product Name" />
          <Input fluid type="text" placeholder="Enter Product Name" />
          <Label className="mt-3 mb-1" content="Product Description" />
          <Input type="text" placeholder="Enter Product Description" fluid />
          <div className="d-flex align-items-center justify-content-between">
            <div>
              <Label className="mt-3 mb-1" content="Product Price" />
              <br />
              <Input min="0" labelPosition='right' type="number" placeholder="Enter Price" >
              <Label basic>$</Label>
              <input />
              <Label>.00</Label>
              </Input>
            </div>
            <div>
              <Label className="mt-3 mb-1" content="Discount" /> <br />
              <Input className="discount-create" type="number" step="5" min="0" max="95" placeholder="Enter Discount" />
            </div>
            <div>
              <Label className="mt-3 mb-1" content="Product Category" /> <br />
              <select placeholder="Please Select Category" name="" id="">
                <option value="" disabled selected>
                  Please Select Category
                </option>
                {categories.map((category: CategoryModel, index) => (
                  <option key={index} value={category.categoryName}>
                    {category.categoryName}
                  </option>
                ))}
              </select>
            </div>
            <div>
              <Label className="mt-3 mb-1" content="Product Colors" /> <br />
              <select placeholder="Please Select Colors" name="" id="">
                <option value="" disabled selected>
                  Please Select Colors
                </option>
                {colors.map((color: ColorModel, index) => (
                  <option key={index} value={color.colorName}>
                    {color.colorName}
                  </option>
                ))}
              </select>
              <br />
            </div>
          </div>

          <Label className="mt-3 " content="Product Images" />
          <br />
          <Input
            className="my-1"
            value={imageUrl}
            onChange={(e) => setImageUrl(e.target.value)}
            fluid
            placeholder="Please Add One or More Image Url One By One"
          />

          <Button onClick={addImageToList}>Add Image</Button>
          <ul>
            {images.map((image: string, index: number) => (
              <li key={index}>{image}</li>
            ))}
          </ul>
          <hr />
          <div className="add-product-btn">
            <Button primary size="big">
              Add Product
            </Button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreateProduct;
