import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import CartItem from "./CartItem";
import { calculateTotals, clearCart } from "./cartSlice";
const CartContainer = () => {
  const { cartItems,total } = useSelector((store: any) => store.cart);
  const dispatch = useDispatch();
  useEffect(()=>{
    dispatch(calculateTotals())
  },[cartItems])
  return (
    <section className="cart-container">
     
      <header>
        <h4 className="text-center">Your Cart</h4>
        <hr />
      </header>
      {total ===0 ? <h3>Your cart is empty</h3> : <></>}
      <div className="cart-items">
        {cartItems.map(({ item, count, color }: any) => (
          <CartItem key={item.productId} item={item} count = {count} color={color} />
        ))}
      </div>
      <footer>
        <hr />
        <div className="d-flex justify-content-around align-items-center">
          <h5 className="">
            Total $<span>{total}</span>
          </h5>
          <button onClick={()=>dispatch(clearCart())} className="clear-cart-btn">Clear Cart</button>
        </div>
      </footer>
    </section>
  );
};

export default CartContainer;
