import React, { useEffect, useState } from "react";
import { Input, Label } from "semantic-ui-react";
import HomePageModel from "../../../Models/HomePage";
const HomePageManagement = () => {
  const [homepageElement, setHomepageElement] = useState<HomePageModel[]>([]);
  const [bannerId, setBannerId] = useState<number>();

  const [httpError, setHttpError] = useState("");

  useEffect(() => {
    const fetchHomeElement = async () => {
      const url = "http://localhost:8080/api/homepages";
      const request = await fetch(url);
      if (!request.ok) {
        throw new Error("Something went wrong!");
      }
      const requestJson = await request.json();
      setHomepageElement(requestJson._embedded.homepages);
    };
    fetchHomeElement().catch((e: any) => setHttpError(e.message));
  }, []);

  let listCarousel = null;
  if (homepageElement.length > 0 && homepageElement[0].carouselImages) {
    const data = homepageElement[0].carouselImages[0];
    const trimmedString = data.slice(1, -1);
    listCarousel = trimmedString
      .split('","')
      .map((item) => item.replace('"', ""));
  }
  return (
    <>
      <div className="content-page">
        <h2 className="text-center">Home Page Management</h2>
        <Label>Home Carousel Images</Label>
        <Input
          placeholder="Add a new Image to Home Page Carousel..."
          className="mt-1 mb-3"
          fluid
        />
        <div className="row">
          {listCarousel?.map((elem: any) => (
            <div className="col-2 selected-images">
              <img src={elem} alt="a" />
            </div>
          ))}
        </div>
        <br />
        <Label>Banner Product ID</Label>
        <Input className="mt-1" fluid />
      </div>
    </>
  );
};

export default HomePageManagement;
