import { Modal, Button, Header, Icon } from "semantic-ui-react";
import React, { useState } from "react";
const ModalUtil:React.FC<{buttonName:string, modalHead:string, modalContent:string}> = (props) => {
  const [open, setOpen] = useState(false);
  return (
    <>
      <Modal
        basic
        onClose={() => setOpen(false)}
        onOpen={() => setOpen(true)}
        open={open}
        size="small"
        trigger={<Button>{props.buttonName}</Button>}
      >
        <h2 className="text-center text-white">{props.modalHead}</h2>
        <Modal.Content>
          <p className="text-center">
            {props.modalContent}
          </p>
        </Modal.Content>
        <Modal.Actions>
          <Button basic color="red" inverted onClick={() => setOpen(false)}>
            <Icon name="remove" /> No
          </Button>
          <Button color="green" inverted onClick={() => {setOpen(false) }}>
            <Icon name="checkmark" onClick/> Yes
          </Button>
        </Modal.Actions>
      </Modal>
    </>
  );
};

export default ModalUtil;
