import { useEffect, useState } from "react";
import ProductModel from "../Models/ProductModel";
import Spinner from "../Utils/Spinner";
import SearchItem from "./Components/SearchItem";
import CategoryModel from "../Models/CategoryModel";

const SearchProductPage = () => {
  const [isLoadingProduct, setIsLoadingProduct] = useState(true);
  const [httpError, setHttpError] = useState(null);
  const [products, setProducts] = useState<ProductModel[]>([]);
  const [totalPages, setTotalPages] = useState(0);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalElements, setTotalElements] = useState(0);
  const [categories, setCategories] = useState<CategoryModel[]>([]);
  const [searchTitle, setSearchTitle] = useState("");
  const [categoryId, setCategoryId] = useState(1);
  const [productCount,setProductCount] = useState([]);
  useEffect(() => {
    const fetchProduct = async () => {
      const url = `http://localhost:8080/api/products?page=${
        currentPage - 1
      }&size=9`;
      const responseData = await fetch(url);
      const responseJson = await responseData.json();
      setProducts(responseJson._embedded.products);
      setTotalElements(responseJson.page.totalElements);
      setTotalPages(responseJson.page.totalPages);
      setIsLoadingProduct(false);
      if (!responseData.ok) {
        throw new Error("Something went wrong during fetching product!");
      }
    };
    fetchProduct().catch((err: any) => {
      setHttpError(err.message);
    });
  }, []);
  useEffect(() => {
    const fetchSearcedItem = async () => {
      const url = `http://localhost:8080/api/products/search/findByProductNameContainingIgnoreCase?productName=${searchTitle}`;
      const responseData = await fetch(url);
      const responseJson = await responseData.json();
      setProducts(responseJson._embedded.products);
      setIsLoadingProduct(false);
      if (!responseData.ok) {
        throw new Error("Something went wrong during fetching product!");
      }
    };
    fetchSearcedItem().catch((err) => setHttpError(err.message));
  }, [searchTitle]);
  useEffect(() => {
    const fetchCategories = async () => {
      const url = "http://localhost:8080/api/categories";
      const responseData = await fetch(url);
      if (!responseData.ok) {
        throw new Error("Something went wrong during fetching product!");
      }
      const responseJson = await responseData.json();
      setCategories(responseJson._embedded.categories);
      setIsLoadingProduct(false);
    };
    fetchCategories().catch((err) => setHttpError(err.message));
  }, []);

  useEffect(() => {
    const fetchProductByCategory = async () => {
      if (categoryId !== 0) {
        const url = categoryId
          ? `http://localhost:8080/api/categories/${categoryId}/products`
          : `http://localhost:8080/api/categories/product`;
        const responseData = await fetch(url);
        if (!responseData.ok) {
          throw new Error("Something went wrong during fetching product!");
        }
        const responseJson = await responseData.json();
        setProducts(responseJson._embedded.products);
        setIsLoadingProduct(false);
      }
    };
    fetchProductByCategory().catch((err) => setHttpError(err.message));
  }, [categoryId]);

  if (isLoadingProduct) {
    return <Spinner />;
  }
  if (httpError) {
    return <>{httpError}</>;
  }

  return (
    <>
      <div>
        <div className="shop-page-wrapper ptb-100">
          <div className="container">
            <div className="row">
              <div className="col-lg-3">
                <div className="shop-sidebar">
                  <div className="sidebar-widget mb-50">
                    <h3 className="sidebar-title">Search Products</h3>
                    <div className="sidebar-search">
                      <form action="#">
                        <input
                          className="search-bar"
                          placeholder="Search Products..."
                          type="text"
                          onChange={(e) => setSearchTitle(e.target.value)}
                        />
                        <button>
                          <svg
                            xmlns="http://www.w3.org/2000/svg"
                            width="16"
                            height="16"
                            fill="currentColor"
                            className="bi bi-search"
                            viewBox="0 0 16 16"
                          >
                            <path d="M11.742 10.344a6.5 6.5 0 1 0-1.397 1.398h-.001c.03.04.062.078.098.115l3.85 3.85a1 1 0 0 0 1.415-1.414l-3.85-3.85a1.007 1.007 0 0 0-.115-.1zM12 6.5a5.5 5.5 0 1 1-11 0 5.5 5.5 0 0 1 11 0z" />
                          </svg>
                        </button>
                      </form>
                    </div>
                  </div>
                  <div className="sidebar-widget mt-3">
                    <h3 className="sidebar-title">Filter by Price</h3>
                    <div className="price_filter">
                      <div id="slider-range"></div>
                      <div className="price_slider_amount">
                        <div className="label-input">
                          <label>price : </label>
                          <input
                            type="text"
                            id="amount"
                            name="price"
                            placeholder="Add Your Price"
                          />
                        </div>
                        <button type="button">Filter</button>
                      </div>
                    </div>
                  </div>
                  <div className="sidebar-widget mb-45">
                    <h3 className="sidebar-title mt-3">Categories</h3>
                    <div className="sidebar-categories">
                      <ul className="m-0 pl-1">
                        {categories.map((category, index) => (
                          <li
                            key={index}
                            className="category-item"
                            onClick={() => setCategoryId(category.categoryId)}
                          >
                            <span className="mb-1"><svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-dash-lg" viewBox="0 0 16 16">
  <path fill-rule="evenodd" d="M2 8a.5.5 0 0 1 .5-.5h11a.5.5 0 0 1 0 1h-11A.5.5 0 0 1 2 8Z"/>
</svg></span>{category.categoryName} 

                          </li>
                        ))}
                      </ul>
                    </div>
                  </div>
                  <div className="sidebar-widget sidebar-overflow mb-45">
                    <h3 className="sidebar-title">color</h3>
                    <div className="product-color">
                      <ul>
                        <li className="red">b</li>
                        <li className="pink">p</li>
                        <li className="blue">b</li>
                        <li className="sky">b</li>
                        <li className="green">y</li>
                        <li className="purple">g</li>
                      </ul>
                    </div>
                  </div>
                </div>
              </div>
              <div className="col-lg-9 order-1 order-lg-2">
                <div className="shop-product-wrapper">
                  <div className="shop-bar-area">
                    <div className="shop-bar pb-60">
                      <div className="shop-found-selector">
                        <div className="shop-found">
                          <p>
                            <span>23</span> Product Found of <span>50</span>
                          </p>
                        </div>
                        <div className="shop-selector">
                          <label>Sort By : </label>
                          <select name="select">
                            <option value="">Default</option>
                            <option value="">A to Z</option>
                            <option value=""> Z to A</option>
                            <option value="">In stock</option>
                          </select>
                        </div>
                      </div>
                      <div className="shop-filter-tab">
                        <div className="shop-tab nav" role="tablist">
                          <a
                            className="active"
                            href="#grid-sidebar7"
                            data-bs-toggle="tab"
                            role="tab"
                            aria-selected="false"
                          >
                            <svg
                              xmlns="http://www.w3.org/2000/svg"
                              width="16"
                              height="16"
                              fill="currentColor"
                              className="bi bi-grid-3x3-gap"
                              viewBox="0 0 16 16"
                            >
                              <path d="M4 2v2H2V2h2zm1 12v-2a1 1 0 0 0-1-1H2a1 1 0 0 0-1 1v2a1 1 0 0 0 1 1h2a1 1 0 0 0 1-1zm0-5V7a1 1 0 0 0-1-1H2a1 1 0 0 0-1 1v2a1 1 0 0 0 1 1h2a1 1 0 0 0 1-1zm0-5V2a1 1 0 0 0-1-1H2a1 1 0 0 0-1 1v2a1 1 0 0 0 1 1h2a1 1 0 0 0 1-1zm5 10v-2a1 1 0 0 0-1-1H7a1 1 0 0 0-1 1v2a1 1 0 0 0 1 1h2a1 1 0 0 0 1-1zm0-5V7a1 1 0 0 0-1-1H7a1 1 0 0 0-1 1v2a1 1 0 0 0 1 1h2a1 1 0 0 0 1-1zm0-5V2a1 1 0 0 0-1-1H7a1 1 0 0 0-1 1v2a1 1 0 0 0 1 1h2a1 1 0 0 0 1-1zM9 2v2H7V2h2zm5 0v2h-2V2h2zM4 7v2H2V7h2zm5 0v2H7V7h2zm5 0h-2v2h2V7zM4 12v2H2v-2h2zm5 0v2H7v-2h2zm5 0v2h-2v-2h2zM12 1a1 1 0 0 0-1 1v2a1 1 0 0 0 1 1h2a1 1 0 0 0 1-1V2a1 1 0 0 0-1-1h-2zm-1 6a1 1 0 0 1 1-1h2a1 1 0 0 1 1 1v2a1 1 0 0 1-1 1h-2a1 1 0 0 1-1-1V7zm1 4a1 1 0 0 0-1 1v2a1 1 0 0 0 1 1h2a1 1 0 0 0 1-1v-2a1 1 0 0 0-1-1h-2z" />
                            </svg>
                          </a>
                          <a
                            href="#grid-sidebar8"
                            data-bs-toggle="tab"
                            role="tab"
                            aria-selected="true"
                          >
                            <svg
                              xmlns="http://www.w3.org/2000/svg"
                              width="16"
                              height="16"
                              fill="currentColor"
                              className="bi bi-list"
                              viewBox="0 0 16 16"
                            >
                              <path
                                fill-rule="evenodd"
                                d="M2.5 12a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5zm0-4a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5zm0-4a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5z"
                              />
                            </svg>{" "}
                          </a>
                        </div>
                      </div>
                    </div>
                    <div className="shop-product-content tab-content">
                      <div
                        id="grid-sidebar7"
                        className="tab-pane fade active show"
                      >
                        <div className="row">
                          {products.map((product) => (
                            <SearchItem
                              product={product}
                              key={product.productId}
                            />
                          ))}
                        </div>
                      </div>
                      
                    </div>
                  </div>
                </div>
                <div className="pagination-style mt-10 text-center">
                  <ul>
                    <li>
                      <a href="#">
                        <i className="ti-angle-left"></i>
                      </a>
                    </li>
                    <li>
                      <a href="#">1</a>
                    </li>
                    <li>
                      <a href="#">2</a>
                    </li>
                    <li>
                      <a href="#">...</a>
                    </li>
                    <li>
                      <a href="#">19</a>
                    </li>
                    <li className="active">
                      <a href="#">
                        <i className="ti-angle-right"></i>
                      </a>
                    </li>
                  </ul>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default SearchProductPage;
