import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { removeItem } from "../cartSlice";

const CartPage = () => {
  const [images, setImages] = useState([]);
  const [httpError, setHttpError] = useState("");
  const [productIdImage , setProductIdImage] = useState([])
  const { cartItems, total } = useSelector((store: any) => store.cart);
  const dispatch = useDispatch();
useEffect(()=>{
  const removeItem = () =>{
    state.cartItems = state.cartItems.filter(
    (item: any) =>
      item.item.productId !== productId || item.color !== color
  );
  }
})
  // useEffect(() => {
  //   const fetchImages = async () => {
  //     const url = `http://localhost:8080/api/v1/products/getImages?productId=${props.item.productId}`;
  //     const responseData = await fetch(url);
  //     const responseJson = await responseData.json();
  //     if (!responseData.ok) {
  //       throw new Error("Something went during fetching images wrong!");
  //     }
  //     setImages(responseJson);
  //   };
  //   fetchImages().catch((err) => {
  //     setHttpError(err.message);
  //   });
  // }, []);
  return (
    <>
      <div className="cart-main-area pt-95 pb-100">
        <div className="container">
          <div className="row">
            <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
              <h1 className="cart-heading">Cart</h1>
              <form action="#">
                <div className="table-content table-responsive">
                  <table>
                    <thead>
                      <tr>
                        <th>remove</th>
                        <th>images</th>
                        <th>Product</th>
                        <th>Price</th>
                        <th>Quantity</th>
                        <th>Total</th>
                      </tr>
                    </thead>
                    <tbody>
                      {cartItems.map((item: any) => (
                        <tr>
                          <td className="product-remove">
                            <button
                           onClick={()=>dispatch(removeItem({productId:item.item.productId,color:item.color}))}
                              className="remove-cart-item"
                            >
                              <svg
                                xmlns="http://www.w3.org/2000/svg"
                                width="16"
                                height="16"
                                fill="currentColor"
                                className="bi bi-trash3-fill"
                                viewBox="0 0 16 16"
                              >
                                <path d="M11 1.5v1h3.5a.5.5 0 0 1 0 1h-.538l-.853 10.66A2 2 0 0 1 11.115 16h-6.23a2 2 0 0 1-1.994-1.84L2.038 3.5H1.5a.5.5 0 0 1 0-1H5v-1A1.5 1.5 0 0 1 6.5 0h3A1.5 1.5 0 0 1 11 1.5Zm-5 0v1h4v-1a.5.5 0 0 0-.5-.5h-3a.5.5 0 0 0-.5.5ZM4.5 5.029l.5 8.5a.5.5 0 1 0 .998-.06l-.5-8.5a.5.5 0 1 0-.998.06Zm6.53-.528a.5.5 0 0 0-.528.47l-.5 8.5a.5.5 0 0 0 .998.058l.5-8.5a.5.5 0 0 0-.47-.528ZM8 4.5a.5.5 0 0 0-.5.5v8.5a.5.5 0 0 0 1 0V5a.5.5 0 0 0-.5-.5Z" />
                              </svg>
                            </button>
                          </td>
                          <td className="product-thumbnail">
                            <a href="#">
                              <img src="assets/img/cart/1.jpg" alt="" />
                            </a>
                          </td>
                          <td className="product-name">
                            <p>{item.item.productName} </p>
                          </td>
                          <td className="product-price-cart">
                            <span className="amount">${item.item.price}</span>
                          </td>
                          <td className="product-quantity">
                            <input value={item.count} min="1" type="number" />
                          </td>
                          <td className="product-subtotal">
                            ${item.count * item.item.price}
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
                {cartItems.length === 0 ? (
                  <p className="error-message text-center">
                    Your Cart is currently empty
                  </p>
                ) : (
                  <>
                    <hr />
                    <div className="row">
                      <div className="col-md-5 ms-auto">
                        <div className="cart-page-total">
                          <h2>Cart totals</h2>
                          <ul>
                            <li>
                              Total<span>${total}</span>
                            </li>
                          </ul>
                          <a href="#">Proceed to checkout</a>
                        </div>
                      </div>
                    </div>
                  </>
                )}
              </form>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default CartPage;
