import React, { useEffect, useState } from "react";
import ProductModel from "../../../Models/ProductModel";
import ModalExampleModal from "../../../Utils/ModalExampleModal";
import { useSelector } from "react-redux";
import CategoryModel from "../../../Models/CategoryModel";
import { fetchProductByAllInputs } from "../../../Utils/SearchFunction";
import Pagination from "../../../Utils/Pagination";
const ProductManagementPage = () => {
  const { isAuthenticated, isAdmin, tokenType, accessToken } = useSelector(
    (store: any) => store.auth
  );

  const [errorMessage, setErrorMessage] = useState("");
  const [products, setProducts] = useState<ProductModel[]>([]);
  const [deleteConfirm, setDeleteConfirm] = useState(0);

  const [totalPages, setTotalPages] = useState(0);
  const [currentPage, setCurrentPage] = useState(1);
  const [productPerPage, setProductPerPage] = useState(9);

  const [totalElements, setTotalElements] = useState(0);
  const [categories, setCategories] = useState<CategoryModel[]>([]);
  const [searchTitle, setSearchTitle] = useState("");
  const [categoryId, setCategoryId] = useState(0);
  const [invalidChar, setInvalidChar] = useState(false);
  const [minPrice, setMinPrice] = useState(0);
  const [maxPrice, setMaxPrice] = useState(10000);
  const [priceError, setPriceError] = useState(false);
  const [gridView, setGridView] = useState(true);
  const [mobile, setMobile] = useState(false);
  const [sortUrl, setSortUrl] = useState("&sort=");

  useEffect(() => {
    try {
      fetchProductByAllInputs(
        searchTitle,
        categoryId,
        minPrice,
        maxPrice,
        setProducts,
        setPriceError,
        currentPage,
        productPerPage,
        setTotalPages,
        setTotalElements,
        sortUrl
      );
      if (window.innerWidth <= 770) {
        setMobile(true);
        setGridView(false);
      }
    } catch (err: any) {
      setErrorMessage(err.message);
    }
  }, [
    searchTitle,
    categoryId,
    minPrice,
    maxPrice,
    currentPage,
    gridView,
    sortUrl,
    deleteConfirm,
  ]);

  const handleSearchInputValidInput = (input: string) => {
    const modifiedInput = input.replace(/[^a-zA-Z0-9 ]/g, "#");
    if (modifiedInput.includes("#")) {
      setInvalidChar(true);
    } else {
      setSearchTitle(input);
      setInvalidChar(false);
    }
  };

  const handleSortUrl= (value:string)=>{
    switch(value){
      case "asc": setSortUrl("&sort=productName,asc"); break;
      case "desc": setSortUrl("&sort=productName,desc"); break;
      case "priceasc": setSortUrl("&sort=price,asc"); break;
      case "pricedesc": setSortUrl("&sort=price,desc"); break;
      case "default": setSortUrl("&sort="); break;
    }
  }

  const handleConfirmDelete = (value: number) => {
    const fetchDeleteProduct = async () => {
      try {
        const url = `http://localhost:8080/api/v1/products/admin/deleteproduct?productId=${value}%20`;
        const requestBody = {
          method: "DELETE",
          headers: {
            Authorization: `${tokenType} ${accessToken}`,
            "Content-Type": "application/json",
          },
        };
        if (isAuthenticated && isAdmin) {
          const request = await fetch(url, requestBody);
          if (!request.ok) {
            throw new Error("Something went wrong during deleting product!");
          }
          const response = await request.json();
          setDeleteConfirm(1);
          alert(response.message);
        }
      } catch (e: any) {
        setErrorMessage(e.message);
      }
    };
    fetchDeleteProduct().catch((e: any) => setErrorMessage(e.message));
    setDeleteConfirm(0);
  };

  if (errorMessage) {
    return <p>{errorMessage}</p>;
  }
  const paginate = (pageNumber: number) => {
    setCurrentPage(pageNumber);
    window.scrollTo({ top: 5, behavior: "smooth" });
  };

  return (
    <>
      <div className="content-page">
        <div className="product-admin-head">
          <button className="btn btn-lg btn-primary waves-effect waves-light my-2 w-100">
            Add Product
          </button>
          <div className="shop-sidebar">
                  <div className="sidebar-widget mb-50">
                    <h3 className="sidebar-title mb-3 mt-2">Search Products</h3>
                    {invalidChar ? (
                      <p className="error-search">
                        Please enter valid product title
                      </p>
                    ) : (
                      <></>
                    )}
                    <div
                      className={
                        invalidChar
                          ? "sidebar-search invalid-search"
                          : "sidebar-search"
                      }
                    >
                      <input
                        className="search-bar"
                        placeholder="Search Products..."
                        type="text"
                        onChange={(e) =>
                          handleSearchInputValidInput(e.target.value)
                        }
                      >
                      </input>
                      <span>
                        <svg
                          xmlns="http://www.w3.org/2000/svg"
                          width="16"
                          height="16"
                          fill="currentColor"
                          className="bi bi-search"
                          viewBox="0 0 16 16"
                        >
                          <path d="M11.742 10.344a6.5 6.5 0 1 0-1.397 1.398h-.001c.03.04.062.078.098.115l3.85 3.85a1 1 0 0 0 1.415-1.414l-3.85-3.85a1.007 1.007 0 0 0-.115-.1zM12 6.5a5.5 5.5 0 1 1-11 0 5.5 5.5 0 0 1 11 0z" />
                        </svg>
                      </span>
                    </div>
                  </div>
                  <div className="sidebar-widget mt-4">
                    <h3 className="sidebar-title mb-3 ">Filter by Price</h3>
                    {priceError ? (
                      <p className="error-search">
                        Max and Min prices should be corresponding order!
                      </p>
                    ) : (
                      <></>
                    )}
                    <div className="price_filter">
                      <div id="slider-range"></div>
                      <div className="price_slider_amount">
                        <div className="label-input d-flex align-items-center">
                          <input
                            type="number"
                            className="amount"
                            name="price"
                            placeholder="Min Price"
                            min={0}
                            onChange={(e) => {
                              setMinPrice(parseInt(e.target.value));
                            }}
                          />
                          <span className="px-2">-</span>
                          <input
                            type="number"
                            className="amount"
                            name="price"
                            placeholder="Max Price"
                            min="0"
                            onChange={(e) => {
                              setMaxPrice(parseInt(e.target.value));
                            }}
                          />
                        </div>
                      </div>
                    </div>
                  </div>
                  <div className="sidebar-widget mb-45">
                    <h3 className="sidebar-title mt-4 mb-3">Categories</h3>
                    <div className="sidebar-categories">
                      <ul className="m-0 p-0 category-item">
                        <li
                          className={categoryId === 0 ? "active-category" : ""}
                        >
                          <span>
                            <svg
                              xmlns="http://www.w3.org/2000/svg"
                              width="16"
                              height="16"
                              fill="currentColor"
                              className="bi bi-dash-lg mb-1"
                              viewBox="0 0 16 16"
                            >
                              <path
                                fillRule="evenodd"
                                d="M2 8a.5.5 0 0 1 .5-.5h11a.5.5 0 0 1 0 1h-11A.5.5 0 0 1 2 8Z"
                              />
                            </svg>
                          </span>
                          <p onClick={() => setCategoryId(0)}>All Categories</p>
                        </li>
                        {categories.map((category, index) => (
                          <li
                            className={
                              categoryId === category.categoryId
                                ? "active-category"
                                : ""
                            }
                            key={index}
                            onClick={() => setCategoryId(category.categoryId)}
                          >
                            <span className="">
                              <svg
                                xmlns="http://www.w3.org/2000/svg"
                                width="16"
                                height="16"
                                fill="currentColor"
                                className="bi bi-dash-lg mb-1"
                                viewBox="0 0 16 16"
                              >
                                <path
                                  fillRule="evenodd"
                                  d="M2 8a.5.5 0 0 1 .5-.5h11a.5.5 0 0 1 0 1h-11A.5.5 0 0 1 2 8Z"
                                />
                              </svg>
                            </span>
                            <p>{category.categoryName}</p>
                          </li>
                        ))}
                      </ul>
                    </div>
                  </div>
                  
                </div>
        </div>
        <div className="table-responsive">
          <table className="table table-hover mb-0">
            <thead>
              <tr>
                <th>#Id</th>
                <th>Name</th>
                <th>Description</th>
                <th>price</th>
                <th>discount</th>
                <th>Discounted Price</th>
                <th>Created At</th>
                <th className="text-center">Manage</th>
              </tr>
            </thead>
            <tbody>
              {products?.map((product: ProductModel) => (
                <tr>
                  <td>{product?.productId}</td>
                  <td>{`${product?.productName.slice(0, 15)}...`}</td>
                  <td>{`${product?.productDescription.slice(0, 20)}...`}</td>
                  <td>
                    <span
                      style={{ fontSize: "14px" }}
                      className="badge bg-success"
                    >
                      {product?.price}$
                    </span>
                  </td>
                  <td>
                    <span
                      style={{ fontSize: "14px" }}
                      className="badge bg-warning"
                    >
                      {product?.discount}%
                    </span>
                  </td>

                  <td>
                    <span
                      className="badge bg-danger"
                      style={{ fontSize: "14px" }}
                    >
                      {product.discountedPrice ? product.discountedPrice : 0}$
                    </span>
                  </td>
                  <td>{product.createdAt}</td>
                  <td>
                    <ModalExampleModal
                      confirmDelete={handleConfirmDelete}
                      buttonName="Delete"
                      modalHeader="Delete Product"
                      modalContent="You clicked to delete the selected product. Are you sure deleting
                       product ? If you clicked Yes your product will be deleted!"
                      productId={product.productId || 0}
                    />
                    <button className="btn btn-outline-warning waves-effect waves-light mx-2">
                      Edit
                    </button>
                    <button className="btn btn-outline-success waves-effect waves-light">
                      Preview
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <div className="my-2">
            {totalElements >= 9 ? (
              <Pagination
                currentPage={currentPage}
                totalPages={totalPages}
                paginate={paginate}
              />
            ) : (
              <></>
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default ProductManagementPage;
