import React, { useEffect, useState } from "react";

const ContactMessage = () => {
  const [messageSenderEmail, setMessageSenderEmail] = useState("");
  const [messageTitle, setMessageTitle] = useState("");
  const [messageSenderName, setMessageSenderName] = useState("");
  const [messageBody, setMessageBody] = useState("");
  const [messageSenderPhone, setMessageSenderPhone] = useState("");

  const [fetchError, setFetchError] = useState(false);
  useEffect(() => {
    const sendMessageToAdmin = async () => {
      try {
        const response = await fetch(
          "http://localhost:8080/api/v1/messages/sendmessage",
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify({
              messageSenderEmail: { messageSenderEmail },
              messageTitle: { messageTitle },
              messageSenderName: { messageSenderName },
              messageBody: { messageBody },
              messageSenderPhone: { messageSenderPhone },
            }),
          }
        );
      } catch {
        setFetchError(true);
        throw new Error("Something went wrong!");
      }
    };
  });

  return (
    <div className="contact-message mt-5">
      <div className="contact-title">
        <h4>Contact Information</h4>
      </div>
      <form
        id="contact-form"
        className="contact-form"
        action="https://htmldemo.net/ezone/ezone/assets/mail.php"
        method="post"
      >
        <div className="row">
          <div className="col-md-6">
            <div className="contact-input-style mb-30">
              <label>Name*</label>
              <input
                name="name"
                type="text"
                onChange={(e) => {
                  setMessageSenderName(e.target.value);
                }}
              />
            </div>
          </div>
          <div className="col-md-6">
            <div className="contact-input-style mb-30">
              <label>Email*</label>
              <input
                name="email"
                type="email"
                onChange={(e) => {
                  setMessageSenderEmail(e.target.value);
                }}
              />
            </div>
          </div>
          <div className="col-md-6">
            <div className="contact-input-style mb-30">
              <label>Telephone</label>
              <input
                name="telephone"
                type="text"
                onChange={(e) => {
                  setMessageSenderPhone(e.target.value);
                }}
              />
            </div>
          </div>
          <div className="col-md-6">
            <div className="contact-input-style mb-30">
              <label>subject</label>
              <input
                name="subject"
                type="text"
                onChange={(e) => {
                  setMessageTitle(e.target.value);
                }}
              />
            </div>
          </div>
          <div className="col-md-12">
            <div className="contact-textarea-style mb-30">
              <label>Comment*</label>
              <textarea
                className="form-control2"
                name="message"
                onChange={(e) => {
                  setMessageBody(e.target.value);
                }}
              ></textarea>
            </div>
            <button className="submit contact-btn btn-hover" type="submit">
              Send Message
            </button>
          </div>
        </div>
      </form>
      <p className="form-messege"></p>
    </div>
  );
};

export default ContactMessage;
