import { Modal, Button, Header, Icon } from "semantic-ui-react";
import React, { useState } from "react";
const ModalExampleModal: React.FC<{ onValueChange: any }> = (props) => {
  const [open, setOpen] = React.useState(false);
  const [inputValue, setInputValue] = useState("");

  const handleChange = (event:any) => {
    const newValue = event.target.value;
    setInputValue(newValue);

    // Call the callback function to send the value to the parent
    props.onValueChange(newValue);
  };
  return (
    <>
      <Modal
        basic
        onClose={() => setOpen(false)}
        onOpen={() => setOpen(true)}
        open={open}
        size="small"
        trigger={<Button>Basic Modal</Button>}
      >
        <h2 className="text-center text-white">Delete Product</h2>
        <Modal.Content>
          <p className="text-center">
            You clicked to delete the selected product. Are you sure deleting
            product ? If you clicked "Yes" your product will be deleted!
          </p>
        </Modal.Content>
        <Modal.Actions>
          <Button basic color="red" inverted onClick={() => setOpen(false)}>
            <Icon name="remove" /> No
          </Button>
          <Button color="green" inverted onClick={() => setOpen(false)}>
            <div>
              
            </div>
            <Icon name="checkmark" onClick/> Yes
          </Button>
        </Modal.Actions>
      </Modal>
    </>
  );
};

export default ModalExampleModal;
