import { Redirect, Route, Switch, useLocation } from "react-router-dom";
import "./App.css";
import HomePage from "./Home/HomePage";
import Footer from "./NavbarFooter/Footer";
import Navbar from "./NavbarFooter/Navbar";
import ProductDetailPage from "./ProductDetail/ProductDetailPage";
import SearchProductPage from "./SearchProduct/SearchProductPage";
import AboutUs from "./AboutUs/AboutUs";
import ContactPage from "./Contact/ContactPage";
import CartPage from "./Cart/Components/CartPage";
import Checkout from "./Cart/Components/Checkout";
import AuthPage from "./Auth/AuthPage";
import AdminPage from "./Admin/AdminPage";
import MessagePage from "./Admin/Components/Messages/MessagePage";

function App() {

  const location = useLocation();
  const isAdminPage = location.pathname.includes('admin') ;

  return (
    <div className="d-flex flex-column min-vh-100">
           {!isAdminPage && <Navbar />}

      <div className="flex-grow-1">
        <Switch>
          <Route path="/" exact>
            <Redirect to="/" />
            <HomePage />
          </Route>
          <Route path="/search">
            <SearchProductPage />
          </Route>
          <Route path="/aboutus">
            <AboutUs />
          </Route>
          <Route path="/contact">
            <ContactPage />
          </Route>
          <Route path="/detail:productId">
            <ProductDetailPage />
          </Route>
          <Route path="/cartpage">
            <CartPage />
          </Route>
          <Route path="/checkout">
            <Checkout />
          </Route>
          <Route path="/authentication/register">
            <AuthPage register={true} />
          </Route>
          <Route path="/authentication/login">
            <AuthPage register={false} />
          </Route>
          <Route path="/admin">
            <AdminPage/> 
          </Route>
          <Route path="/messagePage:messageId">
            <MessagePage/> 
          </Route>
        </Switch>
      </div>
      {!isAdminPage && <Footer />}

    </div>
  );
}

export default App;
