import React, { useEffect, useState } from "react";
const Registration = () => {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [requestMessage, setRequestMessage] = useState("");
  const [status, setStatus] = useState(0);
  const togglePasswordVisibility = () => {
    const passwordInput: any = document.getElementById("user-password");
    const eyeVisibleIcon: any = document.getElementById("eye-visible");
    const eyeUnvisibleIcon: any = document.getElementById("eye-unvisible");

    if (passwordInput.type === "password") {
      passwordInput.type = "text";
      eyeVisibleIcon.style.display = "none";
      eyeUnvisibleIcon.style.display = "block";
    } else {
      passwordInput.type = "password";
      eyeVisibleIcon.style.display = "block";
      eyeUnvisibleIcon.style.display = "none";
    }
  };

  const registerUser = async (event: any) => {
    event?.preventDefault();
    const url = "http://localhost:8080/api/v1/auth/signup";
    try {
      if (firstName && lastName && email && password) {
        const registrationRequestBody = {
          firstName: firstName,
          lastName: lastName,
          email: email,
          password: password,
        };

        const response = await fetch(url, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(registrationRequestBody),
        });

        if (!response.ok) {
          throw new Error("Network request was not ok!");
        }
        const responseData = await response.json();
        setStatus(responseData.status);
        setRequestMessage(responseData.message);
      }
     
    } catch (error: any) {
      setRequestMessage("Error occurred during registration");
    }
  };


  return (
    <div className="register-area ptb-100">
      <div className="container-fluid">
        <div className="row">
          <div className="col-lg-7 col-md-6 col-12 p-0">
            <div className="back-pattern"></div>
          </div>
          <div className="col-md-6 col-12 col-lg-5 col-xl-5 p-0">
            <div className="login">
              <div className="login-form-container">
             
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Registration;
