import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import ProductModel from "../Models/ProductModel";

let cartItems: ProductModel[] = [];

export const getCardItems = createAsyncThunk("cart/getCartItems", async() => {
  const url = "http://localhost:8080/api/products";
  return fetch(url)
    .then((response) =>
      response.json().then((dataJson) => dataJson._embedded.products)
    )
    .catch((err) => console.log(err));
});


const initialState = {
  cartItems: [],
  amount: 5,
  total: 0,
  isLoadingCart: true,
};

const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    clearCard:(state:any)=>{
        state.cartItems=[]
    },
    removeItem:(state:any,action)=>{
      const itemId = action.payload
      state.cartItems=state.cartItems.filter((x:any)=> x.productId !== itemId)
    },
    calculateTotals:(state:any)=>{
      let amount = 0;
      let total = 0;
      state.cartItems.forEach((item:ProductModel) => {
        total += item.price * item.price;
      });
      state.amount = amount;
      state.total = total;
    }
  },
  extraReducers:{
    [String(getCardItems.pending)]:(state:any)=>{
        state.isLoadingCart=true
    },
    [String(getCardItems.fulfilled)]:(state:any,action)=>{
        state.isLoadingCart=false;
        state.cartItems = action.payload
    },
    [String(getCardItems.rejected)]:(state:any)=>{
        state.isLoadingCart=true;
    }
  }
});
export const{clearCard,removeItem,calculateTotals}= cartSlice.actions;
export default cartSlice.reducer;
