import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {AppDispatch} from '../Redux/store'
import CartItem from "./CartItem";
import ProductModel from "../Models/ProductModel";
import { clearCard, getCardItems } from "./cartSlice";
const CartContainer = () => {
  const { amount, cartItems, total } = useSelector((store: any) => store.cart);

const dispatch = useDispatch<AppDispatch>()
  useEffect(()=>{
    dispatch(getCardItems());
  },[])
console.log(cartItems);


  return (
    <section className="cart-container">
      <header>
        <h4 className="text-center">Your Cart</h4>
        <hr />
      </header>
      <div className="cart-items">
      {amount > 0 ? 
        cartItems.map((item:ProductModel)=>(
          <CartItem key={item.productId} item={item}/>
          )) :  <h4 className="text-center">Card is currently empty!</h4>
      }
       </div> 
      <footer>
        <hr />
        <div className="d-flex justify-content-around align-items-center">

        <h5 className="">Total <span>${total}</span></h5>
        <button className="clear-cart-btn" onClick={()=>dispatch(clearCard())}>Clear Cart</button>
        </div>
      </footer>
    </section>
  );
};

export default CartContainer;
