import React, { useEffect, useState } from "react";
import ProductModel from "../../../Models/ProductModel";

const ProductManagementPage = () => {
  const [errorMessage, setErrorMessage] = useState("");
  const [products, setProducts] = useState<ProductModel[]>();

  useEffect(() => {
    const fetchProducts = async () => {
      const url = `http://localhost:8080/api/products`;
      const response = await fetch(url);
      if (!response.ok) {
        throw new Error("Something went wrong during fetching products");
      }
      const responseJson = await response.json();
      setProducts(responseJson._embedded.products);
    };
    fetchProducts().catch((e: any) => {
      setErrorMessage(e.message);
    });
  });
  return (
    <>
      <div className="content-page">
        <div className="">
          <div className="table-responsive">
            <table className="table table-hover mb-0">
              <thead>
                <tr>
                  <th>#Id</th>
                  <th>Name</th>
                  <th>Description</th>
                  <th>price</th>
                  <th>discount</th>
                  <th>Discounted Price</th>
                  <th>Manage</th>
                </tr>
              </thead>
              <tbody>
                {products?.map((product: ProductModel) => (
                  <tr>
                    <td>{product?.productId}</td>
                    <td>{`${product?.productName.slice(0,15)}...`}</td>
                    <td>{`${product?.productDescription.slice(0,20)}...`}</td>
                    <td>{product?.price}</td>
                    <td>{product?.discount}</td>

                    <td>
                      <span className="badge bg-danger">{product.discountedPrice ? product.discountedPrice : 0}</span>
                    </td>
                    <td><span>delete</span>
                    <span>delete</span>
                    <span>delete</span></td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </>
  );
};

export default ProductManagementPage;
