import { createSlice } from "@reduxjs/toolkit";
import ProductModel from "../Models/ProductModel";


let cartItems:ProductModel[]=[]

export const fetchProducts = () => (dispatch: any) => {
  const url = "http://localhost:8080/api/products";
  fetch(url)
    .then((response) => {
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }
      return response.json();
    })
    .then((responseJson) => {
      const fetchedData = responseJson._embedded.products;
      const fetchedCartItems = fetchedData.map((data: ProductModel) => ({
        productId: data.productId,
        productName: data.productName,
        productDescription: data.productDescription,
        discount: data.discount,
        discountedPrice: data.discountedPrice,
        price: data.price,
        rating: data.rating,
      }));
      dispatch(setCartItems(fetchedCartItems));
    })
    .catch((error) => {
      console.error(error);
    });
};

const initialState = {
  cartItems: cartItems,
  amount: 0,
  total: 0,
  isLoadingCart: true,
};

const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {},
});

export default cartSlice.reducer;
