import React, { useEffect, useState } from "react";
import ProductModel from "../../Models/ProductModel";

const RelatedProduct: React.FC<{ relatedProducts: ProductModel[] }> = (
  props
) => {

    const [images, setImages] = useState([]);
    const [isLoadingSingleProduct, setIsLoadingSingleProduct] = useState(true);
    const [httpError, setHttpError] = useState("");

    useEffect(() => {
        const fetchProductImages = async () => {
          const url = `http://localhost:8080/api/v1/products/getImages?productId=${re}`;
          const responseData = await fetch(url);
          if (!responseData.ok) {
            throw new Error("Something went wrong during fetching single product!");
          }
          const responseJson = await responseData.json();
          setImages(responseJson);
          setIsLoadingSingleProduct(false);
        };
        fetchProductImages().catch((err: any) => {
          setHttpError(err.message);
        });
      }, []);
  return (
    <div className="product-area pb-95 mt-5">
      <div className="container">
        <div className="section-title-3 text-center mb-50">
          <h2 className="pb-4">Related products</h2>
        </div>
        <div className="product-style ">
          <div className="row">
            {props.relatedProducts.map((relatedProduct, index) => (
              <div className="col-lg-4 col-md-6">
                <div className="product-img">
                  <a href="#">
                    <img src={require("../../Assets/Images/5.jpg")} alt="" />
                  </a>
                </div>
                <div className="product-content">
                  <h5>
                    <a href="#">{relatedProduct.productName.length > 20
                    ? `${relatedProduct.productName.slice(0, 20)}...`
                    : relatedProduct.productName}{" "}</a>
                  </h5>
                  <span>${relatedProduct.price}</span>
                </div>
              </div>
            ))}

            <div className="col-lg-4 col-md-6">
              <div className="product-img">
                <a href="#">
                  <img src={require("../../Assets/Images/6.jpg")} alt="" />
                </a>
              </div>
              <div className="product-content">
                <h4>
                  <a href="#">Arifo Stylas Dress</a>
                </h4>
                <span>$115.00</span>
              </div>
            </div>
            <div className="col-lg-4 col-md-6">
              <div className="product-img">
                <a href="#">
                  <img src={require("../../Assets/Images/7.jpg")} alt="" />
                </a>
              </div>
              <div className="product-content">
                <h4>
                  <a href="#">Arifo Stylas Dress</a>
                </h4>
                <span>$115.00</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RelatedProduct;
