import { Dispatch, SetStateAction } from "react";
import ProductModel from "../Models/ProductModel";

const fetchProductByAllInputs = async (
  searchTitle: string,
  categoryId: number,
  minPrice: number,
  maxPrice: number,
  setProducts: Dispatch<SetStateAction<ProductModel[]>>,
  setPriceError: Dispatch<SetStateAction<boolean>>,
  setMinPrice: Dispatch<SetStateAction<number>>,
  setMaxPrice: Dispatch<SetStateAction<number>>
) => {
  let url = "";
  if (
    searchTitle !== "" &&
    categoryId !== 0 &&
    minPrice !== 0 &&
    maxPrice !== 10000
  ) {
    url = `http://localhost:8080/api/products/search/findByProductNameContainingIgnoreCaseAndCategoryAndPriceBetween?productName=${searchTitle}&category=${categoryId}%20&minPrice=${minPrice}%20&maxPrice=${maxPrice}%20`;
  } else if (
    searchTitle !== "" &&
    categoryId !== 0 &&
    minPrice === 0 &&
    maxPrice === 10000
  ) {
    url = `http://localhost:8080/api/products/search/findByProductNameContainingIgnoreCaseAndCategory?productName=${searchTitle}&category=${categoryId}%20`;
  } else if (
    searchTitle !== "" &&
    categoryId === 0 &&
    minPrice === 0 &&
    maxPrice === 10000
  ) {
    url = `http://localhost:8080/api/products/search/findByProductNameContainingIgnoreCase?productName=${searchTitle}`;
  } else if (
    searchTitle !== "" &&
    categoryId === 0 &&
    minPrice !== 0 &&
    maxPrice !== 10000
  ) {
    url = `http://localhost:8080/api/products/search/findByProductNameContainingIgnoreCaseAndPriceBetween?productName=${searchTitle}&minPrice=${minPrice}%20&maxPrice=${maxPrice}%20`;
  } else if (
    searchTitle === "" &&
    categoryId !== 0 &&
    minPrice !== 0 &&
    maxPrice !== 10000
  ) {
    url = `http://localhost:8080/api/products/search/findByCategoryAndPriceBetween?category=${categoryId}%20&minPrice=${minPrice}%20&maxPrice=${maxPrice}%20`;
  } else if (
    searchTitle === "" &&
    categoryId !== 0 &&
    minPrice === 0 &&
    maxPrice === 10000
  ) {
    url = `http://localhost:8080/api/products/search/findByCategory?category=${categoryId}%20`;
  } else if (
    searchTitle === "" &&
    categoryId === 0 &&
    minPrice !== 0 &&
    maxPrice !== 10000
  ) {
    url = `http://localhost:8080/api/products/search/findByPriceBetween?minPrice=${minPrice}%20&maxPrice=${maxPrice}%20`;
  } else {
    url = `http://localhost:8080/api/products/`;
  }

  if(minPrice < maxPrice){
    setPriceError(false)
    const responseData = await fetch(url)
    if(!responseData.ok){
      throw new Error("Something went wrong!")
    }
    const responseJson = await responseData.json();
    setProducts(responseJson._embedded.products)
  }else{
    setPriceError(true)
    setMinPrice(0)
    setMaxPrice(10000)
  }

};

export { fetchProductByAllInputs };
