import { createSlice } from "@reduxjs/toolkit";
import ProductModel from "../Models/ProductModel";

// export const getCardItems = createAsyncThunk("cart/getCartItems", async() => {
//   const url = "http://localhost:8080/api/products";
//   return fetch(url)
//     .then((response) =>
//       response.json().then((dataJson) => dataJson._embedded.products)
//     )
//     .catch((err) => console.log(err));
// });

const initialState = {
  cartItems: [],
  total: 0,
  isLoadingCart: true,
  count:0
};

const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    clearCart: (state: any) => {
      state.cartItems = [];
      state.count = 0
    },

    addToCart: (state: any, action) => {
      const { product, cartCount, cartColor } = action.payload;
      let indexOfElem = state.cartItems.findIndex(
        (item: any) => item.item.productId === product.productId
      );
      let indexOfElemColor = state.cartItems.findIndex(
        (item: any) => item.color == cartColor
      );
      if (indexOfElem === -1 || indexOfElemColor ===-1) {
        state.cartItems.push({
          item: product,
          count: cartCount,
          color: cartColor,
        });
        state.count +=cartCount
      }else if(indexOfElemColor !==-1 && indexOfElem !==-1){
        state.cartItems[i].count +=cartCount
        state.count +=cartCount

      }
    },

    removeItem: (state: any, action) => {
      const productId = action.payload;
      const indexOfElem = state.cartItems.findIndex(
        (item: any) => item.item.productId === productId
      );
      const removedCount = state.cartItems[indexOfElem].count
      state.count -= removedCount
      state.cartItems = state.cartItems.filter(
        (item: any, index: number) =>
          item.item.productId !== productId || index !== indexOfElem
      );
    },

    // cartItems = [{productId,n,p,...}]
    calculateTotals: (state: any) => {
      let totalPrice = 0;
      state.cartItems.forEach((cartItem: any) => {
        totalPrice += cartItem.item.price * cartItem.count;
      });

      state.total = totalPrice;
    },
  },
  // extraReducers:{
  //   [String(getCardItems.pending)]:(state:any)=>{
  //       state.isLoadingCart=true
  //   },
  //   [String(getCardItems.fulfilled)]:(state:any,action)=>{
  //       state.isLoadingCart=false;
  //       let count = 1
  //       if(state.cartItems[0].items.filter((x:ProductModel)=> x.productId))
  //       state.cartItems = [{
  //         items:action.payload,
  //         count:count
  //       }]
  //   },
  //   [String(getCardItems.rejected)]:(state:any)=>{
  //       state.isLoadingCart=true;
  //   }
  // }
});
export const { clearCart, removeItem, calculateTotals, addToCart } =
  cartSlice.actions;
export default cartSlice.reducer;
