import React from "react";

const Pagination: React.FC<{
  currentPage: number;
  totalPages: number;
  paginate: any;
}> = (props) => {
  const pageNumbers: number[] = [];
  if (props.currentPage === 1) {
    pageNumbers.push(props.currentPage);
    if (props.totalPages >= props.currentPage + 1) {
      pageNumbers.push(props.currentPage + 1);
    }
    if (props.totalPages >= props.currentPage + 2) {
      pageNumbers.push(props.currentPage + 2);
    }
  } else if (props.currentPage > 1) {
    if (props.currentPage >= 3) {
      pageNumbers.push(props.currentPage - 2);
      pageNumbers.push(props.currentPage - 1);
    } else {
      pageNumbers.push(props.currentPage - 1);
    }
    pageNumbers.push(props.currentPage);

    if (props.totalPages >= props.currentPage + 1) {
      pageNumbers.push(props.currentPage + 1);
    }
    if (props.totalPages >= props.currentPage + 2) {
      pageNumbers.push(props.currentPage + 2);
    }
  }

  return (
    <div>
      <div className="pagination-style mt-10 text-center">
        <ul>
          <li>
            <a  onClick={() => props.paginate(1)}>
              First Page
            </a>
          </li>
          {pageNumbers.map((number) => (
            <li key={number} onClick={() => props.paginate(number)}>
              <a className="pagination page-link">{number}</a>
            </li>
          ))}
          <li
            className="active"
            onClick={() => props.paginate(props.totalPages)}
          >
            <a href="#">Last Page</a>
          </li>
          <li className="page-item">
            <button className="page-link">Last Page</button>
          </li>
        </ul>
      </div>
  
    </div>
  );
};

export default Pagination;
