import { createSlice } from "@reduxjs/toolkit";
import { useState } from "react";
import ProductModel from "../Models/ProductModel";


let cartItems:ProductModel[]=[]

const fetchProducts = async () => {
    const url = "http://localhost:8080/api/products";
    const responseData = await fetch(url);
    if (!responseData.ok) {
      throw new Error("Something went wrong!");
    }
    const responseJson = await responseData.json();
    const fetchedData = responseJson._embedded.products;
    cartItems = fetchedData.map((data: ProductModel) => ({
      productId: data.productId,
      productName: data.productName,
      productDescription: data.productDescription,
      discount: data.discount,
      discountedPrice: data.discountedPrice,
      price: data.price,
      rating: data.rating,
    }));
    
    // Now you can update the cartItems state with the new array
    // Instead of directly modifying the existing array
    // cartItems.push(...newCartItems); // Uncomment this line if you want to add the new items to the existing array
  
    // Or if you want to replace the existing cartItems array completely
    // with the new array, you can do the following:
    // cartItems = newCartItems;
  
    // You can return the new array if needed
    return cartItems;
  };
  
  fetchProducts()
    .then((cartItems) => {
      console.log(cartItems);
      cartItems=cartItems
      // Update the state or do further processing with the new array
    })
    .catch((err) => {
      console.error(err);
    });

const initialState = {
  cartItems: cartItems,
  amount: 0,
  total: 0,
  isLoadingCart: true,
};

const cartSlice = createSlice({
  name: "carts",
  initialState,
  reducers: {},
});

export default cartSlice.reducer;
