import React, { useEffect, useState } from "react";
import { Input, Label } from "semantic-ui-react";

const HomePageManagement = () => {
    const [carouselImages,setCarouselImages] = useState([])
    const [bannerId,setBannerId] = useState<number>()

    const [httpError,setHttpError] = useState("");


  useEffect(() => {
    const fetchHomeElement = async() =>{
        const url = "http://localhost:8080/api/homepages"
        const request = await fetch(url);
        if(!request.ok){
            throw new Error("Something went wrong!");
        }
        const requestJson = await request.json()
        setCarouselImages(requestJson._embedded.homepages.carouselImages)
        setBannerId(requestJson._embedded.homepages.bannerProductId)
    }
    fetchHomeElement().catch((e:any)=>setHttpError(e.message))
  },[]);

  const jsonString = carouselImages[0];
  const listCarousel = JSON.parse(jsonString);
  console.log(listCarousel);

  return (
    <>
      <div className="content-page">
        <h2 className="text-center">Home Page Management</h2>
        <Label>Home Carousel Images</Label>
        <Input className="mt-1 mb-3" fluid />
        <Label>Banner Product ID</Label>
        <Input className="mt-1" fluid />
      </div>
    </>
  );
};

export default HomePageManagement;
