import { createSlice } from "@reduxjs/toolkit";

const initialTodoState = {
  todoItems: [],
  count: 0,
};

const todoSlice = createSlice({
  name: "todo",
  initialState: initialTodoState,
  reducers: {
    addToList: (state: any, action) => {
      const { todoTitle, todoDesc, todoCreatedAt } = action.payload;
      const currentDate = new Date();


      
      const month = currentDate.getMonth() + 1; // Note: Months are zero-based, so January is 0, February is 1, etc.
      const day = currentDate.getDate();
      state.todoItems = state.todoItems.push({
        todoTitle: todoTitle,
        todoDesc: todoDesc,
        todoCreatedAt: todoCreatedAt,
      });
      state.count = state.count + 1;
    },
    clearList: (state: any) => {
      state.todoItems = [];
      state.count = 0;
    },
    removeFromList: (state: any, action) => {
      state.todoItems = state.todoItems.filter(
        (e: any) => e.todoTitle !== action.payload
      );
      state.count = state.count - 1;
    },

    // loginSuccess: (state: any, action) => {
    //   const { user } = action.payload;
    //   state.userEmail = user.email;
    //   state.name = user.firstName;
    //   state.accessToken = user.accessToken;
    //   state.tokenType = user.tokenType;
    //   state.roles = user.roles;
    // },
  },
});
export const { addToList, clearList, removeFromList } = todoSlice.actions;
export default todoSlice.reducer;
