import React, { useEffect, useState } from "react";
import { Button, Input, Label, Select } from "semantic-ui-react";
import CategoryModel from "../../../Models/CategoryModel";

const CreateProduct = () => {
  const [categories, setCategories] = useState<CategoryModel[]>([]);
  const [errorMessage, setErrorMessage] = useState("");
  useEffect(() => {
    const fetchCategories = async () => {
      const url = "http://localhost:8080/api/categories";
      const responseData = await fetch(url);
      if (!responseData.ok) {
        throw new Error("Something went wrong during fetching product!");
      }
      const responseJson = await responseData.json();
      setCategories(responseJson._embedded.categories);
    };
    fetchCategories().catch((err) => setErrorMessage(err.message));
  }, []);

if(errorMessage){
    return <p>{errorMessage}</p>
}
const productCategories = [
    { key: '1', value: 'by', text: 'Belarus' },
    { key: '2', value: 'be', text: 'Belgium' },
    { key: 'bz', value: 'bz', text: 'Belize' },
    { key: 'bj', value: 'bj', text: 'Benin' },
  ]

  return (
    <div className="content-page">
      <div className="create-product-header text-center">
        <h2>Create Product</h2>
        <p>Errors</p>
      </div>
      <div className="create-product-form">
        <form action="">
          <Label className="mt-3 mb-1" content="Product Name" />
          <Input fluid type="text" />
          <Label className="mt-3 mb-1" content="Product Description" />
          <Input type="text" fluid />
          <div className="d-flex align-items-center justify-content-between">
            <div>
              <Label className="mt-3 mb-1" content="Product Price" />
              <br />
              <Input type="number" />
            </div>
            <div>
              <Label className="mt-3 mb-1" content="Discount" /> <br />
              <Input type="number" />
            </div>
            <div>
              <Label className="mt-3 mb-1" content="Product Category" /> <br />
              <Select placeholder="Please Select Category" options={categories}/>
            </div>
          </div>
          <Label className="mt-3 mb-1" content="Product Colors" />
          <Input fluid />
          <Label className="mt-3 mb-1" content="Product Images" />
          <Input fluid />
          <hr />
          <div className="add-product-btn">
            <Button primary size="big">
              Add Product
            </Button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreateProduct;
